Client = require("jsonrpc-node").Client

var client1 = new Client({host:"localhost", port:(process.env.PORT || 3001), path: "/basic"});
var client2 = new Client({host:"localhost", port:(process.env.PORT || 3001), path: "/scientific"});

client2.setBasicAuth("john", "correcthorsebatterystaple");

client1.call("sum", [1,2,3], function(err, result){
    console.log("err=",err," result=",result);
})

client2.call("multiply", [1,2,4], function(err, result){
    console.log("err=",err," result=",result);
})

